import './App.css';
import React, { useState, useEffect } from 'react';
import { getSpotifyToken } from "./spotifyAuth";
import { fetchSpotifyData } from "./spotifyApi";
import Header from './components/Header';
import Searchbar from './components/Searchbar';
import Searchresult from './components/Searchresults';
import Playlist from './components/Playlist';

function App() {
  const [searchQuery, setSearchQuery] = useState('');
const [searchResults, setSearchResults] = useState([]);
const [playlistName, setPlaylistName] = useState('New Playlist'); 
const [playlistTracks, setPlaylistTracks] = useState([]); 
const clientId = "1840d8c49ff1481c8748e3e4188f320f"; 
const clientSecret = "c81dfd43be3746b894a59a9956a1247e"; 

const handleSearch = (query) => {
  setSearchQuery(query);
};

const updatePlaylistName = (name) => {
  setPlaylistName(name);
};

const addTrack = (track) => {
  if (playlistTracks.find((savedTrack) => savedTrack.id === track.id)) return;
  setPlaylistTracks((prevTracks) => [...prevTracks, track]);
};

console.log('Track to add:', track); // Check if track is received correctly
  if (playlistTracks.find((savedTrack) => savedTrack.id === track.id)) {
    console.log('Track already in playlist');
    return; // Prevent adding the same track twice
  }
  setPlaylistTracks((prevTracks) => {
    console.log('Updated playlist:', [...prevTracks, track]); // Log updated state
    return [...prevTracks, track];
  });

const removeTrack = (track) => {
  setPlaylistTracks((prevTracks) =>
    prevTracks.filter((savedTrack) => savedTrack.id !== track.id)
  );
};

 useEffect(() => {
    const fetchData = async () => {
      if (!searchQuery) return;

      try {
        // Step 1: Get Spotify API Token
        const token = await getSpotifyToken(clientId, clientSecret);
        // Step 2: Fetch data from Spotify API
        const fetchedSearchData = await fetchSpotifyData(`/search?q=${searchQuery}&type=track`, token);
        const tracks = fetchedSearchData.tracks?.items || [];
        // Step 3: Update state with the fetched data
        setSearchResults(tracks); // Update state with the fetched data
      } catch (error) {
        console.error("Error fetching Spotify data:", error);
      }
    };

    fetchData(); // Call the async function
  }, [searchQuery]); // 


  return (
    <div>
      <Header/>
      <div>
      <Searchbar onSearch={handleSearch} />
    </div>
      <div className='wrapper'>
      <Searchresult tracks={searchResults} onAdd={addTrack}/>
      <Playlist
        playlistName={playlistName}
        playlistTracks={playlistTracks}
        onNameChange={updatePlaylistName}
        onRemove={removeTrack}

      />
      </div>
    </div>
  );
}

export default App;
