import Searchbar from './Searchbar';
import React, { useState, useEffect } from 'react';
import { getSpotifyToken } from "../spotifyAuth";
import { fetchSpotifyData } from "../spotifyApi";



const Searchresults = async () => {
const [data, setData] = useState(null);
const clientId = "1840d8c49ff1481c8748e3e4188f320f"; // Replace with your Spotify Client ID
const clientSecret = "c81dfd43be3746b894a59a9956a1247e"; // Replace with your Spotify Client Secret    
 const searchEndPoint = 'https://api.spotify.com/v1/search'; 
 const searchQuery = 'Civic';
 const urlToFetch = `${searchEndPoint}?q=${searchQuery}&type=track`;

 useEffect(() => {
    const fetchData = async () => {
      try {
        // Step 1: Get Spotify API Token
        const token = await getSpotifyToken(clientId, clientSecret);
        // Step 2: Fetch data from Spotify API
        const fetchedData = await fetchSpotifyData(urlToFetch, token);
        // Step 3: Update state with the fetched data
          return (
             <div>
            { setData(fetchedData)}
           </div>
            )
      } catch (error) {
        console.error("Error fetching Spotify data:", error);
      }
    };

    fetchData();
  }, [])
}

export default Searchresults
