import React, { useState, useEffect } from 'react';
import { getSpotifyToken } from "./spotifyAuth";
import { fetchSpotifyData } from "./spotifyApi";
import Header from './components/Header';
import Searchbar from './components/Searchbar';
import Searchresult from './components/Searchresults';
import Playlist from './components/Playlist';

function App() {
  const [searchQuery, setSearchQuery] = useState('');
const [searchResults, setSearchResults] = useState();
const clientId = "1840d8c49ff1481c8748e3e4188f320f"; 
const clientSecret = "c81dfd43be3746b894a59a9956a1247e"; 

 useEffect(() => {
    const fetchData = async (query) => {
      try {
        // Step 1: Get Spotify API Token
        const token = await getSpotifyToken(clientId, clientSecret);
        // Step 2: Fetch data from Spotify API
        const fetchedData = await fetchSpotifyData(`https://api.spotify.com/v1/search?q=${query}&type=track`, token);
        const tracks = fetchedData.tracks?.items || [];
        // Step 3: Update state with the fetched data
        setSearchResults(tracks); // Update state with the fetched data
      } catch (error) {
        console.error("Error fetching Spotify data:", error);
      }
    };

    fetchData(); // Call the async function
  }, []); // 


  const handleSearch = (query) => {
    setSearchQuery(query);
    fetchSpotifyData(query); // Fetch data with the search query
  };



