import Searchbar from './Searchbar';
import React, { useState, useEffect } from 'react';
import { getSpotifyToken } from "../spotifyAuth";
import { fetchSpotifyData } from "../spotifyApi";

async function Searchresults(playlistId) {
  const clientId = "1840d8c49ff1481c8748e3e4188f320f"; // Replace with your Spotify Client ID
  const clientSecret = "c81dfd43be3746b894a59a9956a1247e"; // Replace with your Spotify Client Secret

  try {
    // Step 1: Get the token
    const token = await getSpotifyToken(clientId, clientSecret);

    // Step 2: Use the token to fetch playlist details
    const playlistData = await fetchSpotifyData(`/playlists/${playlistId}`, token);

    console.log("Playlist Details:", playlistData);
  } catch (error) {
    console.error("Error fetching playlist details:", error);
  }
}

