import React, { useState, useEffect } from 'react';
import { getSpotifyToken } from "../spotifyAuth";
import { fetchSpotifyData } from "../spotifyApi";

const SaveToSpotify = ({ playlistTracks, playlistName }) => {
    const [isSaving, setIsSaving] = useState(false);
    const [error, setError] = useState(null);
    const clientId = "1840d8c49ff1481c8748e3e4188f320f"; 
const clientSecret = "c81dfd43be3746b894a59a9956a1247e";
const scopes = 'playlist-modify-public playlist-modify-private';
const authUrl = `https://accounts.spotify.com/authorize?client_id=${clientId}&response_type=code&redirect_uri=${redirectUri}&scope=${scopes}`;


const code = new URLSearchParams(window.location.search).get('code');

const getToken = async (code) => {
  const response = await fetch('https://accounts.spotify.com/api/token', {
    method: 'POST',
    headers: {
      'Authorization': `Basic ${btoa(clientId + ':' + clientSecret)}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    },
    body: new URLSearchParams({
      grant_type: 'authorization_code',
      code,
      redirect_uri: redirectUri,
    }),
  });
  const data = await response.json();
  return data.access_token;  // Return the access token
};

const accessToken = getToken(code);

  // Function to create a new playlist
  const createPlaylist = async (accessToken) => {
    try {
      // Get user ID
      const userData = await fetchSpotifyData('/me', accessToken);  // Using your fetchSpotifyData function
      const userId = userData.id;

      // Create new playlist
      const createPlaylistResponse = await fetchSpotifyData(`/users/${userId}/playlists`, accessToken, {
        method: 'POST',
        body: JSON.stringify({
          name: playlistName,
          description: 'A custom playlist from Jammming!',
          public: true,
        }),
      });

      const playlistId = createPlaylistResponse.id;

      // Step 2: Add tracks to the newly created playlist
      await addTracksToPlaylist(playlistId, accessToken);
    } catch (err) {
      setError(err.message);
      console.error('Error creating playlist:', err);
    }
  };

  // Function to add tracks to the playlist
  const addTracksToPlaylist = async (playlistId, accessToken) => {
    try {
      const trackUris = playlistTracks.map((track) => track.uri);

      const addTracksResponse = await fetch(`https://api.spotify.com/v1/playlists/${playlistId}/tracks`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${accessToken}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          uris: trackUris,  // Pass the track URIs
        }),
      });

      if (!addTracksResponse.ok) {
        throw new Error('Error adding tracks to playlist');
      }

      console.log('Tracks added successfully!');
    } catch (err) {
      setError(err.message);
      console.error('Error adding tracks:', err);
    }
  };

  // Handle the Save To Spotify button click
  const handleSaveToSpotify = async () => {
    setIsSaving(true);
    setError(null);

    try {

      if (!accessToken) {
        throw new Error('Failed to obtain access token');
      }

      // Step 2: Create playlist and add tracks after getting the token
      await createPlaylist(accessToken);
    } catch (err) {
      setError(err.message);
      console.error('Error saving playlist:', err);
    }

    setIsSaving(false);
  };

  return (
    <div>
      <button onClick={handleSaveToSpotify} disabled={isSaving}>
        {isSaving ? 'Saving to Spotify...' : 'Save To Spotify'}
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default SaveToSpotify;
