import { useState } from "react";
import Tracklist from "./Tracklist";
import Spotify from "./SaveToSpotify";
import "../track.css";

function Playlist({ playlistName, playlistTracks, onNameChange, onRemove, onAdd }) {
  const [successMessage, setSuccessMessage] = useState("");

  const handleNameChange = (e) => {
    onNameChange(e.target.value); // Update playlist name
  };

  const handleSavePlaylist = async () => {
    const success = await Spotify.savePlaylist(playlistName, playlistTracks.map(track => track.uri));
    if (success) {
      setSuccessMessage("✅ Playlist saved successfully!");
      setTimeout(() => {
        clearPlaylist();
      }, 2000);
    } else {
      setSuccessMessage("❌ Failed to save playlist. Try again.");
    }
  };

  return (
    <div className="your-playlist">
      <h2>Create your Playlist</h2>
      <input type="text" value={playlistName} onChange={handleNameChange} />
      <Tracklist tracks={playlistTracks} onRemove={onRemove} isRemoval={true} onAdd={onAdd} />
      <button onClick={handleSavePlaylist} className="save">Save To Spotify</button>
      <p className="success-message">{successMessage}</p>
    </div>
  );
}

export default Playlist;
