export async function fetchSpotifyData(endpoint, token) {
    const baseUrl = "https://api.spotify.com/v1"; // Spotify API base URL
    const url = `${baseUrl}${endpoint}`; // Combine base URL with the endpoint

    const requestOptions = {
      method: "GET", // Use GET to fetch data
      headers: {
        Authorization: `Bearer ${token}`, // Include the token in the Authorization header
      },
    };

    try {
      const response = await fetch(url, requestOptions);
      if (!response.ok) {
        throw new Error(`Spotify API error: ${response.status} ${response.statusText}`);
      }
      const data = await response.json(); // Parse the JSON response
      return data; // Return the data
    } catch (error) {
      console.error("Error fetching Spotify API data:", error);
      throw error; // Propagate the error to the caller
    }
  }
