import React, { useState, useEffect } from 'react';
import { getSpotifyToken } from "../spotifyAuth";

const SaveToSpotify = ({ playlistTracks, playlistName }) => {
    const [isSaving, setIsSaving] = useState(false);
    const [error, setError] = useState(null);
    const clientId = "1840d8c49ff1481c8748e3e4188f320f"; 
const clientSecret = "c81dfd43be3746b894a59a9956a1247e"; 
const token = getSpotifyToken(clientId, clientSecret);

    const createPlaylist = async () => {
        try {
            const fetchedSearchData = await fetchSpotifyData(`/search?q=${searchQuery}&type=track`, token)
         const response = await fetch('https://api.spotify.com/v1/me', {
            method: 'GET',
            headers: {
              Authorization: `Bearer ${token}`,  // Use the access token here
            },
          });

          const userData = await response.json();
          const userId = userData.id;  // User's Spotify ID

          // Create the new playlist
          const createPlaylistResponse = await fetch(`https://api.spotify.com/v1/users/${userId}/playlists`, {
            method: 'POST',
            headers: {
              Authorization: `Bearer ${token}`,
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              name: playlistName,  // Playlist name passed from props
              description: 'A custom playlist from Jammming!',
              public: true,
            }),
          });

          if (!createPlaylistResponse.ok) {
            throw new Error('Error creating playlist');
          }

          const playlistData = await createPlaylistResponse.json();
          const playlistId = playlistData.id;  // Newly created playlist ID

          // Step 2: Add tracks to the newly created playlist
          await addTracksToPlaylist(playlistId);
        } catch (err) {
          setError(err.message);
          console.error('Error creating playlist:', err);
        }
      };

      // Step 2: Add tracks to the new playlist
      const addTracksToPlaylist = async (playlistId) => {
        try {
          // Get the track URIs from the playlistTracks
          const trackUris = playlistTracks.map(track => track.uri);

          // Make a request to add the tracks to the new playlist
          const addTracksResponse = await fetch(`https://api.spotify.com/v1/playlists/${playlistId}/tracks`, {
            method: 'POST',
            headers: {
              Authorization: `Bearer ${token}`,
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              uris: trackUris,  // Pass the track URIs
            }),
          });

          if (!addTracksResponse.ok) {
            throw new Error('Error adding tracks to playlist');
          }

          console.log('Tracks added successfully!');
        } catch (err) {
          setError(err.message);
          console.error('Error adding tracks:', err);
        }
      };

      // Handle the Save To Spotify button click
      const handleSaveToSpotify = () => {
        setIsSaving(true);
        setError(null);
        createPlaylist();  // Call the createPlaylist function
      };

      return (
        <div>
          <button onClick={handleSaveToSpotify} disabled={isSaving}>
            {isSaving ? 'Saving to Spotify...' : 'Save To Spotify'}
          </button>

          {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
      );
    };

    export default SaveToSpotify;
