import Tracklist from './Tracklist';
import React, { useState } from "react";
import SaveToSpotify from './SaveToSpotify'

function Playlist( { playlistName, playlistTracks, onNameChange, onRemove, onAdd, trackUris } ) {

    const handleNameChange = (e) => {
        onNameChange(e.target.value); // Update playlist name in parent
      };

      const handleSave = () => {
        SaveToSpotify.savePlaylist(playlistName, trackUris);
      };

      this.state = {
        playlistName: "My Playlist",
        trackUris: [], // Initialize as an empty array
      };


      return (
        <div>
          <input
            type="text"
            value={playlistName}
            onChange={handleNameChange}
          />
          <Tracklist tracks={playlistTracks} onRemove={onRemove} isRemoval={true} onAdd={onAdd} />
          <button onClick={handleSave}>Save To Spotify</button>
        </div>
    )
}

export default Playlist
